components/common/customizations/navigat:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

<div class="dropdown-panel mega-nav-panel">
<div class="container">
<div class="mega-nav-landing-wrapper">

<div class="mega-nav-landing-list-wrapper">
<ul class="mega-nav-list {{#if theme_settings.wrap-meganav}}wrap-cols{{/if}}">

{{#each nav_items}}
  {{#startsWith "_" name}}{{ else }}
  <li class="parent {{#if children}}has-children tier-dropdown{{/if}}">
    <a itemprop="url" href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}><span itemprop="name">{{name}}</span></a>
    {{#if children}}
      <ul class="children">
        {{#each children}}
        {{#startsWith "_" name}}{{ else }}
          <li>
            <a itemprop="url" href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}><span itemprop="name">{{name}}</span></a>
            {{#if children}}
              <ul>
                {{#each children}}
                  {{> components/common/customizations/site-navigation-submenus}}
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/startsWith}}
        {{/each}}
      </ul>
    {{/if}}
  </li>
  {{/startsWith}}
{{/each}}
</ul></div>

</div>
</div>

components/common/customizations/site-na:
{{#startsWith "_" name}}{{ else }}
<li {{#if children}}class="tier-dropdown"{{/if}}>
  <a {{#if children}}class="tier-toggle"{{/if}} itemprop="url" href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}><span itemprop="name">{{name}}</span></a>
  {{#if children}}
    <ul class="tier-panel">
      <li class="parent-all"><a href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}>{{lang 'common.navigation_all'}} {{name}}</a></li>
      {{#each children}}
        {{> components/common/customizations/site-navigation-submenus}}
      {{/each}}
    </ul>
  {{/if}}
</li>
{{/startsWith}}
